class Minesweeper {
    constructor(width = 8, height = 8, mines = 10) {
        this.width = width;
        this.height = height;
        this.mines = mines;
        this.board = [];
        this.revealed = new Set();
        this.flagged = new Set();
        this.gameOver = false;
        this.init();
    }

    init() {
        // 创建空板
        for (let y = 0; y < this.height; y++) {
            this.board[y] = [];
            for (let x = 0; x < this.width; x++) {
                this.board[y][x] = 0;
            }
        }

        // 随机放置地雷
        let minesPlaced = 0;
        while (minesPlaced < this.mines) {
            const x = Math.floor(Math.random() * this.width);
            const y = Math.floor(Math.random() * this.height);
            if (this.board[y][x] !== -1) {
                this.board[y][x] = -1;
                minesPlaced++;
            }
        }

        // 计算周围地雷数
        for (let y = 0; y < this.height; y++) {
            for (let x = 0; x < this.width; x++) {
                if (this.board[y][x] === -1) continue;
                this.board[y][x] = this.countAdjacentMines(x, y);
            }
        }
    }

    countAdjacentMines(x, y) {
        let count = 0;
        for (let dy = -1; dy <= 1; dy++) {
            for (let dx = -1; dx <= 1; dx++) {
                const newY = y + dy;
                const newX = x + dx;
                if (newY >= 0 && newY < this.height && newX >= 0 && newX < this.width) {
                    if (this.board[newY][newX] === -1) count++;
                }
            }
        }
        return count;
    }

    reveal(x, y) {
        if (this.gameOver || this.flagged.has(`${x},${y}`)) return;

        const key = `${x},${y}`;
        if (this.revealed.has(key)) return;

        this.revealed.add(key);
        const cell = document.querySelector(`[data-x="${x}"][data-y="${y}"]`);
        cell.classList.add('revealed');

        if (this.board[y][x] === -1) {
            this.gameOver = true;
            cell.classList.add('mine');
            this.showFailurePopup();
            this.revealAll();
            return;
        }

        cell.textContent = this.board[y][x] || '';

        if (this.board[y][x] === 0) {
            for (let dy = -1; dy <= 1; dy++) {
                for (let dx = -1; dx <= 1; dx++) {
                    const newY = y + dy;
                    const newX = x + dx;
                    if (newY >= 0 && newY < this.height && newX >= 0 && newX < this.width) {
                        this.reveal(newX, newY);
                    }
                }
            }
        }

        if (this.checkWin()) {
            alert('牛逼');
            this.gameOver = true;
        }
    }

    showFailurePopup() {
        const overlay = document.createElement('div');
        overlay.className = 'popup-overlay';
        
        const message = document.createElement('div');
        message.className = 'popup-message';
        message.textContent = '傻逼梅佳';
        
        overlay.appendChild(message);
        document.body.appendChild(overlay);
        
        // 播放失败音效
        const failSound = new Audio('data:audio/wav;base64,');
        failSound.play();
        
        // 3秒后自动移除弹窗
        setTimeout(() => {
            overlay.remove();
        }, 3000);
    }

    revealAll() {
        for (let y = 0; y < this.height; y++) {
            for (let x = 0; x < this.width; x++) {
                const key = `${x},${y}`;
                if (!this.revealed.has(key)) {
                    this.revealed.add(key);
                    const cell = document.querySelector(`[data-x="${x}"][data-y="${y}"]`);
                    cell.classList.add('revealed');
                    if (this.board[y][x] === -1) {
                        cell.classList.add('mine');
                    } else {
                        cell.textContent = this.board[y][x] || '';
                    }
                }
            }
        }
    }

    checkWin() {
        const totalCells = this.width * this.height;
        return this.revealed.size === totalCells - this.mines;
    }
}

// 游戏初始化
let game;

function initGame() {
    const board = document.getElementById('board');
    board.innerHTML = '';
    game = new Minesweeper();

    for (let y = 0; y < game.height; y++) {
        for (let x = 0; x < game.width; x++) {
            const cell = document.createElement('div');
            cell.className = 'cell';
            cell.dataset.x = x;
            cell.dataset.y = y;
            cell.addEventListener('click', () => game.reveal(x, y));
            board.appendChild(cell);
        }
    }
}

document.getElementById('new-game').addEventListener('click', initGame);
initGame();
